import React, { useContext } from 'react';
import { Box, Container } from '@mui/material';
import CustomAppBar from '../components/AppBar';
import ProfileCard from "../components/ProfileCard";
import { SettingsContext } from '../context/SettingsContext';
import MarkdownViewer from "../components/MarkdownViewer";

const Home = () => {
  const { language, themeMode, toggleLanguage, toggleTheme } = useContext(SettingsContext);

  return (
    <Box
      sx={{
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
      }}
    >
      {/* AppBar 部分 */}
      <CustomAppBar />

      {/* 主内容部分 */}
      <Container
        maxWidth="lg"
        sx={{
          display: 'flex',
          alignItems: 'flex-start', // 顶部对齐
          justifyContent: 'space-between', // 水平分布空间
          position: 'relative', // 确保定位有效
          paddingTop: '64px', // 避免与 AppBar 重叠
        }}
      >
        {/* ProfileCard 部分 */}
        <Box
          sx={{
            flex: '1 1 40%',
            position: 'absolute', // 使用绝对定位
            top: '50%', // 距离容器顶部 50%
            transform: 'translateY(-50%)', // 上移自身高度的 50% 以实现垂直居中
            marginRight: '20px',
          }}
        >
          <ProfileCard />
        </Box>

        {/* MarkdownViewer 部分 */}
        <Box
          sx={{
            flex: '1 1 60%',
            marginLeft: 'auto', // 将右侧内容推到左边卡片右边
            marginTop: '64px', // 避免与顶部重叠
          }}
        >
          <MarkdownViewer filePath="md/luoxisteven.md" />
        </Box>
      </Container>
    </Box>
  );
};

export default Home;
