import React, { useContext } from 'react';
import { Box, Container } from '@mui/material';
import CustomAppBar from '../components/AppBar';
import ProfileCard from "../components/ProfileCard";
import { SettingsContext } from '../context/SettingsContext';
import MarkdownViewer from "../components/MarkdownViewer";

const Home = () => {
  const { language, themeMode, toggleLanguage, toggleTheme } = useContext(SettingsContext); // 获取当前语言

  return (
    <Box
      sx={{
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
      }}
    >
      {/* AppBar 部分 */}
      <CustomAppBar />

      {/* 主内容部分 */}
      <Container
        maxWidth="lg"
        sx={{
          display: 'flex',
          alignItems: 'flex-start', // 垂直顶部对齐
          justifyContent: 'space-between', // 水平分布空间
          marginTop: '40px', // 减去 AppBar 的高度
          paddingTop: '20px',
        }}
      >
        {/* ProfileCard 部分 */}
        <Box sx={{ flex: '1 1 40%', marginRight: '20px' }}> {/* 占据40%的宽度 */}
          <ProfileCard />
        </Box>

        {/* MarkdownViewer 部分 */}
        <Box sx={{ flex: '1 1 60%' }}> {/* 占据60%的宽度 */}
          <MarkdownViewer filePath="md/luoxisteven.md" />
        </Box>
      </Container>
    </Box>
  );
};

export default Home;
