// Home.js
import React, { useContext } from 'react';
import { SettingsContext } from '../context/SettingsContext';

const Home = () => {
  const { language, theme, toggleLanguage, toggleTheme } = useContext(SettingsContext);

  return (
    <div>
      <h1>{language === 'en' ? 'Welcome!' : '欢迎！'}</h1>
      <p>The current theme is {theme === 'light' ? 'Light Mode' : 'Dark Mode'}.</p>
      <button onClick={toggleLanguage}>Toggle Language</button>
      <button onClick={toggleTheme}>Toggle Theme</button>
    </div>
  );
};

export default Home;
