import React, { useEffect, useState } from "react";
import axios from "axios";
import ReactMarkdown from "react-markdown";
import remarkMath from "remark-math";
import rehypeKatex from "rehype-katex";
import { Box, Typography } from "@mui/material";
import "katex/dist/katex.min.css";

const MarkdownViewer = ({ filePath }) => {
  const [markdownContent, setMarkdownContent] = useState("");

  useEffect(() => {
    const loadMarkdown = async () => {
      try {
        const response = await axios.get(filePath);
        setMarkdownContent(response.data);
      } catch (error) {
        setMarkdownContent("无法加载指定的 Markdown 文件。");
        console.error(error);
      }
    };

    if (filePath) {
      loadMarkdown();
    }
  }, [filePath]);

  return (
    <Box sx={{ p: 4, maxWidth: "800px", margin: "0 auto" }}>
      <Box
        sx={{
          border: "1px solid #e1e4e8",
          borderRadius: "6px",
          p: 3,
        }}
      >
        {markdownContent ? (
          <ReactMarkdown
            children={markdownContent}
            remarkPlugins={[remarkMath]}
            rehypePlugins={[rehypeKatex]}
            components={{
              h1: ({ node, ...props }) => (
                <Typography
                  variant="h4"
                  gutterBottom
                  sx={{
                    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
                    fontWeight: 600,
                    fontSize: "2em",
                    lineHeight: 1.25,
                    marginBottom: "1em", // 增加标题和正文之间的间距
                  }}
                  {...props}
                />
              ),
              h2: ({ node, ...props }) => (
                <Typography
                  variant="h5"
                  gutterBottom
                  sx={{
                    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
                    fontWeight: 600,
                    fontSize: "1.2em",
                    lineHeight: 1.25,
                  }}
                  {...props}
                />
              ),
              h3: ({ node, ...props }) => (
                <Typography
                  variant="h6"
                  gutterBottom
                  sx={{
                    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
                    fontWeight: 600,
                    fontSize: "1.25em",
                    lineHeight: 1.25,
                    marginBottom: "0.75em",
                  }}
                  {...props}
                />
              ),
              p: ({ node, ...props }) => (
                <Typography
                  variant="body1"
                  gutterBottom
                  sx={{
                    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
                    fontSize: "1em",
                    lineHeight: 1.6, // GitHub 的标准段落行间距
                    marginBottom: "1em", // 段落底部的间距
                  }}
                  {...props}
                />
              ),
              li: ({ node, ...props }) => (
                <Typography
                  component="li"
                  variant="body1"
                  sx={{
                    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
                    fontSize: "1em",
                    lineHeight: 1.6, // 与段落保持一致
                    marginBottom: "0.5em", // 列表项之间的间距
                  }}
                  {...props}
                />
              ),
              blockquote: ({ node, ...props }) => (
                <Box
                  component="blockquote"
                  sx={{
                    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
                    fontSize: "1em",
                    lineHeight: 1.6,
                    borderLeft: "4px solid #dfe2e5",
                    paddingLeft: "1em",
                    margin: "1.5em 0",
                  }}
                  {...props}
                />
              ),
              code: ({ node, inline, ...props }) => (
                <Box
                  component="code"
                  sx={{
                    fontFamily: "SFMono-Regular, Consolas, 'Liberation Mono', Menlo, Courier, monospace",
                    fontSize: "0.875em",
                    padding: inline ? "0.2em 0.4em" : "1em",
                    borderRadius: "6px",
                    display: inline ? "inline" : "block",
                    whiteSpace: "pre-wrap", // 自动换行而不是滚动
                    marginBottom: "1em",
                  }}
                  {...props}
                />
              ),
            }}
          />
        ) : (
          <Typography
            variant="body1"
            sx={{
              fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
              lineHeight: 1.6,
            }}
          >
            正在加载内容...
          </Typography>
        )}
      </Box>
    </Box>
  );
};

export default MarkdownViewer;
