import React, { useEffect, useState } from "react";
import axios from "axios";
import Markdown from "mui-markdown";
import { Box, Typography } from "@mui/material";

const MarkdownViewer = ({ filePath }) => {
  const [markdownContent, setMarkdownContent] = useState("");

  useEffect(() => {
    const loadMarkdown = async () => {
      try {
        const response = await axios.get(filePath);
        setMarkdownContent(response.data);
      } catch (error) {
        setMarkdownContent("无法加载指定的 Markdown 文件。");
        console.error(error);
      }
    };

    if (filePath) {
      loadMarkdown();
    }
  }, [filePath]);

  return (
    <Box sx={{ p: 4, margin: "0 auto" }}>
      <Box
        sx={{
          borderRadius: "8px",
          p: 2,
        }}
      >
        {markdownContent ? (
          <Markdown
            overrides={{
              h1: {
                component: Typography,
                props: { variant: "h5", gutterBottom: true, sx: { fontWeight: "bold" } },
              },
              h2: {
                component: Typography,
                props: { variant: "h6", gutterBottom: true, sx: { fontWeight: 600 } },
              },
              h3: {
                component: Typography,
                props: { variant: "subtitle1", gutterBottom: true, sx: { fontWeight: 600 } },
              },
              p: {
                component: Typography,
                props: { variant: "body2", gutterBottom: true, sx: { fontSize: "0.95rem" } },
              },
              li: {
                component: Typography,
                props: { component: "li", variant: "body2", gutterBottom: true, sx: { fontSize: "0.95rem" } },
              },
            }}
          >
            {markdownContent}
          </Markdown>
        ) : (
          <Typography variant="body2" color="textSecondary">
            正在加载内容...
          </Typography>
        )}
      </Box>
    </Box>
  );
};

export default MarkdownViewer;
